#if FIVEMusing CitizenFX.Core;using CitizenFX.Core.Native;using System.Collections.Generic;using System.Threading.Tasks;using Script = CitizenFX.Core.BaseScript;#elif SHVDN3using GTA;using System.Windows.Forms;#endifusing System;using LemonUI;namespace LeMi.Client;/// <summary>/// The Base Script for Lemon's Modding Tools./// </summary>public class LeMi : Script{    #region Fields    private readonly ObjectPool pool = [];    private readonly MenuMain mainMenu = new MenuMain();        #endregion        #region Constructors    /// <summary>    /// Creates a new base script.    /// </summary>    public LeMi()    {        pool.Add(mainMenu);        #if FIVEM        API.RegisterKeyMapping("waypoint", "Teleport to Waypoint", "KEYBOARD", "DELETE");        #endif                Tick += OnTick;                #if SHVDN3        KeyDown += OnKeyDown;        #endif    }    #endregion        #region Event Function    #if FIVEM    private async Task OnTick()    #elif SHVDN3    private void OnTick(object sender, EventArgs e)    #endif    {    }    #if SHVDN3    private void OnKeyDown(object sender, KeyEventArgs e)    {        switch (e.KeyCode)        {            case Keys.Delete:                Teleport.ToWaypoint();                break;        }    }    #endif        #endregion        #region Commands    #if FIVEM    [Command("dv")]    private void DeleteVehicleCommand(int source, List<object> args, string raw) => Vehicles.DeleteCurrent();    [Command("fix")]    private void FixVehicleCommand(int source, List<object> args, string raw) => Vehicles.FixCurrent();    [Command("spawn")]    private async Task SpawnVehicleCommand(int source, List<object> args, string raw)    {        if (args.Count > 0)        {            await Vehicles.SpawnVehicle(args[0].ToString(), true);        }    }    [Command("waypoint")]    private async Task TeleportWaypointCommand(int source, List<object> args, string raw) => await Teleport.ToWaypoint();    [Command("revive")]    private void ReviveCommand(int source, List<object> args, string raw) => PlayerPed.Revive();    #endif    #endregion}