#if FIVEMusing CitizenFX.Core;using Script = CitizenFX.Core.BaseScript;#elif SHVDN3using GTA;#endifusing System;using System.Collections.Generic;using System.Threading.Tasks;using LemonUI;namespace LeMi.Client;/// <summary>/// The Base Script for Lemon's Modding Tools./// </summary>public class LeMi : Script{    #region Fields    private readonly ObjectPool pool = [];        #endregion        #region Constructors    /// <summary>    /// Creates a new base script.    /// </summary>    public LeMi()    {        Tick += OnTick;    }        #endregion        #region Event Function    #if FIVEM    private async Task OnTick()    #elif SHVDN3    private void OnTick(object sender, EventArgs e)    #endif    {    }        #endregion        #region Commands    #if FIVEM    [Command("dv")]    private void DeleteVehicleCommand(int source, List<object> args, string raw) => Vehicles.DeleteCurrent();    [Command("spawn")]    private async Task SpawnVehicleCommand(int source, List<object> args, string raw)    {        if (args.Count > 0)        {            await Vehicles.SpawnVehicle(args[0].ToString(), true);        }    }    #endif    #endregion}